  const base64String = btoa(String.fromCharCode(...new Uint8Array(arrayBuffer)));
  const payload = {
    image: base64String
  };
  fetch('http://localhost:5000/v2/densenet_predict', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(payload)
