export default {
	fastApiServer: "http://localhost:8000/yolo_predict",

	async callApifunc() {
		const imageUrl = img_document.image;

		// Fetch the image data from the URL
		const response = await fetch(imageUrl);
		const blob = await response.blob(); // Convert the response to a blob (binary data)

		// Convert blob to an array buffer to get binary data
		const arrayBuffer = await blob.arrayBuffer();

		// Convert the binary data to a base64 stringa
		const base64String = btoa(String.fromCharCode(...new Uint8Array(arrayBuffer)));

		// Create a payload with the base64 string
		const payload = {
			image: base64String
		};

		// Send the base64 string in the request
		fetch('http://localhost:8000/yolo_predict_base64', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify(payload),
		})
			.then(response => response.json())
			.then(data => {
			resultImage.setImage(data.image);
		})
			.catch(error => console.error('Error:', error));
	}
}

